  const add = async (req, res) => {
    const {
      body,
      decoded
    } = req;
    const t = await sequelize.transaction();

    try {
      var data = {
        id_produk: body.id_produk,
        id_koperasi: decoded.koperasi_id,
        id_member: body.id_member,
        id_ao: decoded.id,
